CREATE VIEW public.view_mrrhh_cesta_ticket_cementerio AS
 SELECT a.id,
    a.requisicion,
    a.ano,
    a.mes,
    a.id_tipo_personal,
    a.cedula,
    ( SELECT mrrhh_personal.nombres
           FROM public.mrrhh_personal
          WHERE (a.cedula = mrrhh_personal.cedula)
         LIMIT 1) AS nombres,
    ( SELECT mrrhh_personal.apellidos
           FROM public.mrrhh_personal
          WHERE (a.cedula = mrrhh_personal.cedula)
         LIMIT 1) AS apellidos,
    ( SELECT mrrhh_personal.status
           FROM public.mrrhh_personal
          WHERE (a.cedula = mrrhh_personal.cedula)
         LIMIT 1) AS status,
    ( SELECT view_mrrhh_personal.ente_adscrito
           FROM public.view_mrrhh_personal
          WHERE (a.cedula = view_mrrhh_personal.cedula)
         LIMIT 1) AS dependencia,
    a.dias_trabajados,
    a.dias_faltados,
    a.dias_a_cancelar,
    a.monto,
    a.procesado,
    ( SELECT view_mrrhh_fusion_personal.numero_cuenta
           FROM public.view_mrrhh_fusion_personal
          WHERE (a.cedula = view_mrrhh_fusion_personal.cedula)
         LIMIT 1) AS numero_cuenta
   FROM public.mrrhh_cesta_ticket_cementerio a
  ORDER BY a.id;


